import helpers from './chart-helpers-overload'
import ChartController from './chart-controller-overload'
import SceneChart from './chart-overload'
import clone from './clone'

var { Component, RectPath } = scene

Chart.defaults.global.defaultFontSize = 10
Chart.defaults.global.tooltips.mode = 'label';

const NATURE = {
  mutable: false,
  resizable: true,
  rotatable: true,
  properties : [{
    type: 'chartjs-properties',
    label: '',
    name: 'chart'
  }]
}

const BASE64_IMAGES = {
  doughnut: 'data:image/png;base64,',
  pie : 'data:image/png;base64,'
}

const DOUGHNUT_IMAGE = new Image()
const PIE_IMAGE = new Image()
DOUGHNUT_IMAGE.src = BASE64_IMAGES.doughnut
PIE_IMAGE.src = BASE64_IMAGES.pie

const SAMPLE_IMAGES = {
  doughnut: DOUGHNUT_IMAGE,
  pie: PIE_IMAGE
}

export default class ChartJSWrapper extends RectPath(Component) {

  _draw(context) {

    var {
      left,
      top,
      width,
      height
    } = this.bounds;

    context.beginPath();
    context.rect(left, top, width, height);
    this.drawFill(context);
    this.drawStroke(context);

    context.closePath();

    if(!this._chart) {
      var { chart, data } = this.model

      if(chart) {
        this.initChart(context)
      }

    }

    var { width, height, left, top } = this.model;

    var self = this;

    var data = this.get('data')

    if(data) {
      this._chart.data.rawData = this.convertObject(data)
    } else {
      this.drawSampleImage(context)
    }

    context.translate(left, top);


    if(!this._draw_once) {
      this._chart.reset(width, height, context);
      this._chart.update(0);
      this._draw_once = true;
    } else {
      if(this._chart.chart.ctx != context){
        this._chart.reset(width, height, context);
      }

      this._chart.draw(this._chart.__ease__);

    }

    context.translate(-left, -top);

  }

  get nature() {
    return NATURE
  }

  get hasTextProperty() {
    return false
  }

  get sampleImage() {

    if(!this.model.chart || !this.model.chart.type)
      return null

    return SAMPLE_IMAGES[this.model.chart.type]
  }

  initChart(context) {

    var {chart} = this.model

    if(chart.options){
      this.convertOptions(chart)
    }

    this._chart = new SceneChart(context,
      clone(chart),
      this
    )
  }

  destroyChart() {
    if(this._chart)
      this._chart.destroy()
    this._chart = null
  }

  convertObject(dataArray) {
    if(!dataArray)
      return null

    if(!(dataArray instanceof Array)) {
      // is not Array
      if(dataArray instanceof Object) {
        return dataArray
      }
      return null
    }

    if(dataArray.length === 0) {
      return null
    }

    // modeling중 변수 기본값에 대한 처리
    if(dataArray[0].hasOwnProperty('__field1')) {
      dataArray = this.toObjectArrayValue(dataArray)
    }

    let label = this.model.chart.data.labelDataKey
    let seriesKeys = []

    for(let i in this.model.chart.data.datasets) {
      seriesKeys.push(this.model.chart.data.datasets[i].dataKey)
    }

    let seriesData = []
    let labelData = []

    let convertedObject = {
      seriesData: seriesData,
      labelData: labelData
    }

    for(let i in dataArray) {
      let currData = dataArray[i]
      labelData.push(currData[label])

      for(let i in seriesKeys) {
        if(!seriesData[i])
          seriesData[i] = []
        seriesData[i].push(currData[seriesKeys[i]])
      }
    }

    return convertedObject
  }

  toObjectArrayValue(array) {
    if(!array || array.length === 0)
      return null

    let indexKeyMap = {}
    let value = []

    for(let key in array[0]) {
      indexKeyMap[key] = array[0][key]
    }

    for(var i = 1; i < array.length; i++) {
      let object = {}
      let thisObject = array[i]
      for(let key in indexKeyMap) {
        let k = indexKeyMap[key];
        let v = thisObject[key];
        object[k] = v
      }

      value.push(object)
    }

    return value
  }

  convertOptions(chart) {
    this.setStacked(chart.options)
    this.setMultiAxis(chart)
    this.setFontSize(chart.options)
    this.setTheme(chart.options)
    this.appendTooltipCallback(chart.options)
  }

  setStacked(options) {
    if(!options)
      return

    var stacked = options.stacked

    if(!options.scales)
      return

    if(options.scales.xAxes){
      for(let axis of options.scales.xAxes) {
        axis.stacked = stacked
      }
    }

    if(options.scales.yAxes){
      for(let axis of options.scales.yAxes) {
        axis.stacked = stacked
      }
    }
  }

  setMultiAxis(chart) {
    if(!chart)
      return

    var options = chart.options
    if(!options)
      return

    var multiAxis = options.multiAxis

    if(!options.scales)
      return

    if(!options.scales.yAxes)
      return

    var datasets = chart.data.datasets
    if(multiAxis){

      if(options.scales.yAxes.length === 1) {
        options.scales.yAxes.push({
          position: 'right',
          id: 'right',
          ticks: {
            beginAtZero:true,
            callback: function(value, index, values) {
              var returnValue = value
              if(typeof returnValue == 'number') {
                returnValue = returnValue.toLocaleString()
              }

              return returnValue
            }
          }
        })
      }
    } else {
      if(datasets) {
        for(let dataset of datasets) {
          if(dataset.yAxisID == 'right')
            dataset.yAxisID = 'left'
        }
      }

      if(options.scales.yAxes.length > 1) {
        options.scales.yAxes = [options.scales.yAxes[0]]
      }
    }
  }

  setTheme(options) {
    if(!options)
      return

    var theme = options.theme

    let darkColor = "#000"
    let lightColor = "#fff"

    var baseColor;

    switch(theme) {
      case 'light' :
        baseColor = lightColor
        break;
      case 'dark' :
      default:
        baseColor = darkColor
        break;
    }

    baseColor = tinycolor(baseColor)

    var isDark = baseColor.isDark();

    var operatorFunction = isDark ? "brighten" : "darken"

    if(!options.legend)
      options.legend = {}

    if(!options.legend.labels)
      options.legend.labels = {}

    options.legend.labels.fontColor = baseColor.clone().setAlpha(.5).toString();

    var scale = options.scales || options.scale

    if(!scale)
      return

    if(scale.xAxes){
      for(let axis of scale.xAxes) {
        if(!axis.gridLines)
          axis.gridLines = {}

        axis.gridLines.display = options.xGridLine
        axis.gridLines.zeroLineColor = baseColor.clone().setAlpha(.5).toString();
        axis.gridLines.color = baseColor.clone().setAlpha(.1).toString();

        if(!axis.ticks)
          axis.ticks = {}

        axis.ticks.fontColor = baseColor.clone().setAlpha(.5).toString();
        this.appendTickCallback(axis.ticks);
      }
    }

    if(scale.yAxes){
      for(let index in scale.yAxes) {
        let axis = scale.yAxes[index]
        if(!axis.gridLines)
          axis.gridLines = {}

        axis.gridLines.display = index == 0 ? options.yGridLine : options.y2ndGridLine
        axis.gridLines.zeroLineColor = baseColor.clone().setAlpha(.5).toString();
        axis.gridLines.color = baseColor.clone().setAlpha(.1).toString();

        if(!axis.ticks)
          axis.ticks = {}

        axis.ticks.fontColor = baseColor.clone().setAlpha(.5).toString();
        this.appendTickCallback(axis.ticks);
      }
    }

  }

  setFontSize(options) {
    if(!options)
      return

    var fontSize = options.fontSize

    let darkColor = "#000"
    let lightColor = "#fff"

    if(!options.legend)
      options.legend = {}

    if(!options.legend.labels)
      options.legend.labels = {}

    options.legend.labels.fontSize = fontSize;

    var scale = options.scales || options.scale

    if(!options.tooltips)
      options.tooltips = {}

    options.tooltips.titleFontSize = options.tooltips.bodyFontSize = options.tooltips.footerFontSize = fontSize;

    if(!scale)
      return

    if(scale.xAxes){
      for(let axis of scale.xAxes) {
        if(!axis.ticks)
          axis.ticks = {}

        let autoMin = axis.ticks.autoMin
        let autoMax = axis.ticks.autoMax

        if(autoMin === true) {
          delete axis.ticks.min
        }
        if(autoMax === true) {
          delete axis.ticks.max
        }

        axis.ticks.fontSize = fontSize
      }
    }

    if(scale.yAxes){
      for(let axis of scale.yAxes) {
        if(!axis.ticks)
          axis.ticks = {}

        let autoMin = axis.ticks.autoMin
        let autoMax = axis.ticks.autoMax

        if(autoMin === true) {
          delete axis.ticks.min
        }
        if(autoMax === true) {
          delete axis.ticks.max
        }

        axis.ticks.fontSize = fontSize;
      }
    }

  }

  appendTickCallback(ticks) {
    ticks.callback = function(value, index, values) {
      var returnValue = value
      if(typeof returnValue == 'number') {
        returnValue = returnValue.toLocaleString()
      }

      if(returnValue)
        return returnValue
    }
  }

  appendTooltipCallback(options) {
    if(!options)
      return

    options.tooltips = Object.assign({}, options.tooltips, {
      callbacks: {
        label : function(tooltipItem, data) {
          var label = data.labels[tooltipItem.index]
          var value = data.datasets[tooltipItem.datasetIndex].data[tooltipItem.index]
          var toNumValue = Number(value)

          if(!Number.isNaN(toNumValue)) {
            value = toNumValue
          }

          if(value)
            return value.toLocaleString()
        }
      }
    })
  }

  drawSampleImage(context) {

    var sampleImage = this.sampleImage

    if(!sampleImage)
      return

    var imageWidth = Math.min(sampleImage.width, this.model.width);
    var imageHeight = Math.min(sampleImage.height, this.model.height);


    context.drawImage(
      sampleImage,
      0,
      0,
      sampleImage.width,
      sampleImage.height,
      this.model.left,
      this.model.top,
      imageWidth,
      imageHeight
      // image.width,
      // image.height
    );

  }

  onchange(after) {

    var isChartChanged = false

    var keys = Object.getOwnPropertyNames(after)
    var key = keys && keys[0]
    var keySplit = key.split(".")

    if (after.hasOwnProperty('chart') || key[0] == 'chart') {
      isChartChanged = true;
    }

    if(keySplit.length > 1) {
      // for(var i in keySplit) {
      //   var k = keySplit[i]
      // }

      delete this.model[key]
    }

    if(after.hasOwnProperty('data')) {
      // this.model.data = after.data
      if(this._chart) {
        this._chart.config.data.rawData = after.data || {};
        this._chart.update()
      }
    }

    if(isChartChanged)
      this.destroyChart()

    this._draw_once = false;
    this.invalidate()

  }

  onclick(e) {
    e.chartJSWrapper = this;
    if(this._chart){
      this._chart.eventHandler(e)
      // console.log('elements', this._chart.getElementsAtEvent(e));
      // console.log('dataset', this._chart.getDatasetAtEvent(e));
    }
  }

  ondragstart(e) {

  }

  onmousemove(e) {
    e.chartJSWrapper = this;
    if(this._chart)
      this._chart.eventHandler(e)
  }
}

Component.register('chartjs', ChartJSWrapper)
